  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"bienvenidos-a-touka"}}>{`Bienvenidos a Touka`}</MDXTag>
<MDXTag name="p" components={components}>{`Touka es un framework open source para la construcción de aplicaciones web del lado del navegador. Esta escrito en javascript usando las librerias react y redux.`}</MDXTag>
<MDXTag name="p" components={components}>{`Touka dispone de todo lo necesario para la creación de una aplicacion, de tipo React, ya configurado y preparado para usarlo.`}</MDXTag>
<MDXTag name="p" components={components}>{`Touka te proporciona:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"/gestion-react"}}>{`Gestion de React`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Gestion de redux`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Gestion de rutas`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Gestion de peticiones http`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Gestion de autenticacion`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Gestion de i18n`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Componentes Basicos`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"instalacion-versión-alpha-solo-apta-para-pruebas"}}>{`instalacion `}<MDXTag name="strong" components={components} parentName="h2">{`Versión Alpha, solo apta para pruebas`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-bash"}}>{`npm install touka
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`o con yarn:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-bash"}}>{`yarn add touka
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"uso"}}>{`uso`}</MDXTag>
<MDXTag name="p" components={components}>{`Para usar Touka necesitamos una aplicación react, puede ser una creada con el cli de react 'create-react-app'.`}</MDXTag>
<MDXTag name="p" components={components}>{`Una vez que tenemos nuestra aplicacion react operativa, simplemente importamos la funcion `}<MDXTag name="a" components={components} parentName="p" props={{"href":"docs/renderApplication.md"}}>{`createAppTouka`}</MDXTag>{` de touka, y le pasamos un objeto con la configuración de la app.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`import createAppTouka from 'touka';
import clients from './clients/module';
import ClientFile from './clients/pages/create-client-page';

createAppTouka({
  defaultLanguage: "es",
  languages: ["es", "en"],
  modules: [
    clients
  ],
  config: {
    noMatchComponent: ClientFile
  }
});
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
